    [84] = {["id"] = 84, ["talk"] = "太太，大小姐走了！", ["char"] = {40019, 40020}, ["name"] = "旁白", ["talking"] = 0, ["next"] = {85}, ["stance"] = {1, 3}, ["dpos"] = {-309, -309}, ["sense"] = 2001},
    [85] = {["id"] = 85, ["talk"] = "床上晕厥的太太悠悠转醒，长叹了一声。", ["char"] = {40019, 40020}, ["name"] = "柳氏", ["talking"] = 40020, ["next"] = {86}, ["stance"] = {1, 3}, ["dpos"] = {-309, -309}, ["sense"] = 2001},
