    [89] = {["id"] = 89, ["talk"] = "老爷不会怪我的，我也是为了我们的女儿。", ["char"] = {40019, 40020}, ["name"] = "柳氏", ["talking"] = 40020, ["next"] = {90}, ["stance"] = {1, 3}, ["dpos"] = {-309, -309}, ["sense"] = 2001},
    [90] = {["id"] = 90, ["talk"] = "……是。", ["char"] = {40019, 40020}, ["name"] = "春雨", ["talking"] = 40019, ["next"] = {91}, ["stance"] = {1, 3}, ["dpos"] = {-309, -309}, ["sense"] = 2001},
    [91] = {["id"] = 91, ["talk"] = "小姐，近日因为老爷的事，府中可乱了……", ["char"] = {40021}, ["name"] = "青湖", ["talking"] = 40021, ["next"] = {92}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [92] = {["id"] = 92, ["talk"] = "噤声！", ["char"] = {40021}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {93}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [93] = {["id"] = 93, ["talk"] = "不远处有两人行踪诡秘，窃窃私语。", ["name"] = "旁白", ["talking"] = 0, ["next"] = {94}, ["sense"] = 2001},
    [94] = {["id"] = 94, ["talk"] = "你快去把寻家的太太给请过来，就说我们大小姐回来了。", ["char"] = {40019, 40022}, ["name"] = "春雨", ["talking"] = 40019, ["next"] = {95}, ["stance"] = {1, 3}, ["dpos"] = {-309, -309}, ["sense"] = 2001},
    [95] = {["id"] = 95, ["talk"] = "刚刚看大小姐也是个厉害的，我们这么做，大小姐会不会……", ["char"] = {40019, 40022}, ["name"] = "仆从", ["talking"] = 40022, ["next"] = {96}, ["stance"] = {1, 3}, ["dpos"] = {-309, -309}, ["sense"] = 2001},
    [96] = {["id"] = 96, ["talk"] = "闭嘴，办事去！太太沉舟破釜才寻得的机会，岂容你这奴才嚼舌！此次若是不能把大小姐嫁给寻家，老爷的牢狱之苦就白受了！", ["char"] = {40019, 40022}, ["name"] = "春雨", ["talking"] = 40019, ["next"] = {97}, ["stance"] = {1, 3}, ["dpos"] = {-309, -309}, ["sense"] = 2001},
    [97] = {["id"] = 97, ["talk"] = "这！！！", ["char"] = {40021}, ["name"] = "青湖", ["talking"] = 40021, ["next"] = {98}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [98] = {["id"] = 98, ["talk"] = "嘘——派个小丫头去门口候着，看看那寻家太太来是有何要事。", ["char"] = {40021}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {99}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [99] = {["id"] = 99, ["talk"] = "是，小姐，我这就去。", ["char"] = {40021}, ["name"] = "青湖", ["talking"] = 40021, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [100] = {["id"] = 100, ["talk"] = "不多时，就有一个小丫鬟低着头跑进院子来找青湖。", ["name"] = "旁白", ["talking"] = 0, ["next"] = {101}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [101] = {["id"] = 101, ["talk"] = "青湖低着头与她窃窃私语了一番，发现我在看她，她就冲我点了点头，小心对我做了几个口型。", ["char"] = {40021}, ["name"] = "旁白", ["talking"] = 0, ["next"] = {102}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [102] = {["id"] = 102, ["talk"] = "小姐——人走啦——", ["char"] = {40021}, ["name"] = "青湖", ["talking"] = 40021, ["next"] = {103}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [103] = {["id"] = 103, ["talk"] = "我招了招手，青湖急忙跑过来。", ["char"] = {40021}, ["name"] = "旁白", ["talking"] = 0, ["next"] = {104}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [104] = {["id"] = 104, ["talk"] = "小姐！大事不好啦！太太寻这寻家太太是来谈亲事的！", ["char"] = {40021}, ["name"] = "青湖", ["talking"] = 40021, ["next"] = {105}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [105] = {["id"] = 105, ["talk"] = "亲事？谁的亲事？——我的？", ["char"] = {40021}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {106}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [106] = {["id"] = 106, ["talk"] = "当然是您的！", ["char"] = {40021}, ["name"] = "青湖", ["talking"] = 40021, ["next"] = {107}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [107] = {["id"] = 107, ["talk"] = "青湖急得满头都是汗。", ["char"] = {40021}, ["name"] = "青湖", ["talking"] = 40021, ["next"] = {108}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [108] = {["id"] = 108, ["talk"] = "慢慢说，不急。", ["char"] = {40021}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {109}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [109] = {["id"] = 109, ["talk"] = "小姐，太太实在是太可恨了！那寻家太太奴婢已经打听清楚了，是扬州的寻县令的夫人，不过是个七品之家，太太还说要在七天内完婚，这简直……太可气了！", ["char"] = {40021}, ["name"] = "青湖", ["talking"] = 40021, ["next"] = {110}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [110] = {["id"] = 110, ["talk"] = "太太是不是与寻太太说，我府正当大难，唯有将我嫁出才能避此一难，而那寻太太虽面有难色，却依然点头相应？", ["char"] = {40021}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {111}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [111] = {["id"] = 111, ["talk"] = "小姐你怎么知道的？", ["char"] = {40021}, ["name"] = "青湖", ["talking"] = 40021, ["next"] = {112}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [112] = {["id"] = 112, ["talk"] = "我依稀记得我母亲曾有一闺中密友，仿佛就是嫁给了一户姓寻的人家。", ["char"] = {40021}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {113}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [113] = {["id"] = 113, ["talk"] = "青湖张口结舌。", ["char"] = {40021}, ["name"] = "青湖", ["talking"] = 40021, ["next"] = {114}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [114] = {["id"] = 114, ["talk"] = "这……这……这寻家太太还是来做好事的不成？", ["char"] = {40021}, ["name"] = "青湖", ["talking"] = 40021, ["next"] = {115}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [115] = {["id"] = 115, ["talk"] = "我意味深长的点了点头。", ["char"] = {40021}, ["next"] = {116}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [116] = {["id"] = 116, ["talk"] = "可不是。", ["char"] = {40021}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {117}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [117] = {["id"] = 117, ["talk"] = "让我来猜一猜……", ["char"] = {40021}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {118}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [118] = {["id"] = 118, ["talk"] = "父亲牢狱之灾，太太破釜沉舟……就是为了在七天内把我嫁出去？我嫁出去有什么好处？七天？为什么七天？超过七天，怕是太太就做不了主了吧？", ["char"] = {40021}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {119}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [119] = {["id"] = 119, ["talk"] = "我顿了一下，脸上忍不住露出了一抹玩味的笑意。", ["char"] = {40021}, ["name"] = "旁白", ["talking"] = 0, ["next"] = {120}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [120] = {["id"] = 120, ["talk"] = "青湖，我回来之前，宫中可有什么要事？", ["char"] = {40021}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {121}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [121] = {["id"] = 121, ["talk"] = "……宫中？", ["char"] = {40021}, ["name"] = "青湖", ["talking"] = 40021, ["next"] = {122}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [122] = {["id"] = 122, ["talk"] = "奴婢想起来了！前阵子宫中传来消息说要选秀女，因您常年不在家中，百花使便选了二小姐……", ["char"] = {40021}, ["name"] = "青湖", ["talking"] = 40021, ["next"] = {123}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [123] = {["id"] = 123, ["talk"] = "那我回来了呢？", ["char"] = {40021}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {124}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [124] = {["id"] = 124, ["talk"] = "有则选其优……您是说？", ["char"] = {40021}, ["name"] = "青湖", ["talking"] = 40021, ["next"] = {125}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [125] = {["id"] = 125, ["talk"] = "怕就是如此了……", ["char"] = {40021}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {126}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [126] = {["id"] = 126, ["talk"] = "我想了想，突然想到了天牢、贿赂还有举报我父亲的官员。", ["char"] = {40021}, ["name"] = "旁白", ["talking"] = 0, ["next"] = {127}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [127] = {["id"] = 127, ["talk"] = "太太一介深闺妇人，到底还是……见识浅了些。", ["char"] = {40021}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {128}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [128] = {["id"] = 128, ["talk"] = "那您还不赶紧去找太太问个清楚？难道就任由太太摆布么？", ["char"] = {40021}, ["name"] = "青湖", ["talking"] = 40021, ["next"] = {129, 130}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [129] = {["id"] = 129, ["talk"] = "你觉得太太与我父亲入狱有关吗？", ["char"] = {40021}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {131}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [130] = {["id"] = 130, ["talk"] = "你的意思是我现在就去见太太？", ["char"] = {40021}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {133}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [131] = {["id"] = 131, ["talk"] = "奴婢不敢说，小姐心中定然已经有了定论。", ["char"] = {40021}, ["name"] = "青湖", ["talking"] = 40021, ["next"] = {132}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [132] = {["id"] = 132, ["talk"] = "我微微点头，如我爹那般的老狐狸，怎会因为收受贿赂入狱？", ["char"] = {40021}, ["name"] = "旁白", ["talking"] = 0, ["next"] = {136}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [133] = {["id"] = 133, ["talk"] = "老爷身陷牢中，小姐还是与太太讲明白才好……", ["char"] = {40021}, ["name"] = "青湖", ["talking"] = 40021, ["next"] = {134}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [134] = {["id"] = 134, ["talk"] = "我似笑非笑的看了她一眼。", ["char"] = {40021}, ["name"] = "旁白", ["talking"] = 0, ["next"] = {135}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [135] = {["id"] = 135, ["talk"] = "你以为我们家太太是个聪明的？我爹那般的人物会因为这些小事就被关入天牢？定然有其他势力插手此事。", ["char"] = {40021}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {136}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [136] = {["id"] = 136, ["talk"] = "不管怎么说，小姐还是再去见一回太太吧。", ["char"] = {40021}, ["name"] = "青湖", ["talking"] = 40021, ["next"] = {137}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [137] = {["id"] = 137, ["talk"] = "不急，先帮我换一身衣裳吧——回家这么久，还未来得及换上一身干净的衣裳，真是失礼极了。", ["char"] = {40021}, ["name"] = "青湖", ["talking"] = 40021, ["next"] = {138}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [138] = {["id"] = 138, ["talk"] = "青湖焦躁的脸上一下子平静下来，服侍我换上了我许久未穿过的、崭新的十六幅长裙。", ["char"] = {40021}, ["name"] = "旁白", ["talking"] = 0, ["next"] = {139}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [139] = {["id"] = 139, ["talk"] = "——青云教绾头上髻,明月与作耳边珰。", ["char"] = {40021}, ["name"] = "旁白", ["talking"] = 0, ["next"] = {140}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [140] = {["id"] = 140, ["talk"] = "师傅曾与我说过，到什么样的地方，就该穿什么样的衣服，说什么样的话，做什么样的人。", ["char"] = {40021}, ["name"] = "旁白", ["talking"] = 0, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [141] = {["id"] = 141, ["talk"] = "太太此时已然清醒了，半倚在引枕上，春雨正服侍她喝着一盏冰糖燕窝。", ["name"] = "旁白", ["talking"] = 0, ["next"] = {142}, ["sense"] = 2001},
    [142] = {["id"] = 142, ["talk"] = "见过太太。", ["char"] = {40020}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {143}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [143] = {["id"] = 143, ["talk"] = "大小姐来了？春雨，搬个椅子给大小姐。", ["char"] = {40020}, ["name"] = "柳氏", ["talking"] = 40020, ["next"] = {144}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [144] = {["id"] = 144, ["talk"] = "谢过太太。", ["char"] = {40020}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {145}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [145] = {["id"] = 145, ["talk"] = "大小姐此来，是否知晓了寻家一事？", ["char"] = {40020}, ["name"] = "柳氏", ["talking"] = 40020, ["next"] = {146}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [146] = {["id"] = 146, ["talk"] = "正是，劳太太忧心了。", ["char"] = {40020}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {147}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [147] = {["id"] = 147, ["talk"] = "听到我的回答，太太那张柔美的脸上露出一点欣慰的笑容。", ["char"] = {40020}, ["name"] = "旁白", ["talking"] = 0, ["next"] = {148}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [148] = {["id"] = 148, ["talk"] = "寻太太，是姐姐的闺蜜，那时候你还小，怕是不记得。", ["char"] = {40020}, ["name"] = "柳氏", ["talking"] = 40020, ["next"] = {149}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [149] = {["id"] = 149, ["talk"] = "我记得，毕竟寻这个姓氏这么少见——太太莫不是想说，寻太太与我娘有指腹为婚之约，如今家逢大难，蒙人不弃，故而要将我嫁出逃过此劫？", ["char"] = {40020}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {150}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [150] = {["id"] = 150, ["talk"] = "大小姐能明白我这番苦心是最好的……就是太委屈大小姐了。", ["char"] = {40020}, ["name"] = "柳氏", ["talking"] = 40020, ["next"] = {151}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [151] = {["id"] = 151, ["talk"] = "我看了看柳氏的脸，那张脸上充满了不舍与怜惜。", ["char"] = {40020}, ["name"] = "柳氏", ["talking"] = 40020, ["next"] = {152}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [152] = {["id"] = 152, ["talk"] = "也不算委屈，自古锦上添花易，雪中送炭难。寻太太愿意在我们家遭逢大难之际履行婚约，当真也是有情有义。虽只是七品之家，却也是难得的风光霁月了。", ["char"] = {40020}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {153}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [153] = {["id"] = 153, ["talk"] = "太太的眼中一下子蓄满了泪水。", ["char"] = {40020}, ["name"] = "旁白", ["talking"] = 0, ["next"] = {154}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [154] = {["id"] = 154, ["talk"] = "大小姐能出落得这么懂事，姐姐若是泉下有知，怕也是能无憾了。", ["char"] = {40020}, ["name"] = "柳氏", ["talking"] = 40020, ["next"] = {155}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [155] = {["id"] = 155, ["talk"] = "太太，您可知父亲受贿一事始末？", ["char"] = {40020}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {156}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [156] = {["id"] = 156, ["talk"] = "柳氏的眼神闪动了一下，有些不自然的挪开了目光。", ["char"] = {40020}, ["name"] = "旁白", ["talking"] = 0, ["next"] = {157}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [157] = {["id"] = 157, ["talk"] = "大小姐为何突然询问此事？我一深宅妇人实在是不懂官场上的事情。", ["char"] = {40020}, ["name"] = "柳氏", ["talking"] = 40020, ["next"] = {158}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [158] = {["id"] = 158, ["talk"] = "既然如此，我也不便多说与太太。", ["char"] = {40020}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {159}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
    [159] = {["id"] = 159, ["talk"] = "太太，我府正当危难，还望太太约束下人，谨守府中，莫要失了我南府应有的气度。", ["char"] = {40020}, ["name"] = "玩家", ["talking"] = -1, ["next"] = {160}, ["stance"] = {2}, ["dpos"] = {-309}, ["sense"] = 2001},
